import fetch from './fetch';
export default {
  getPages: function getPages() {
    return fetch("
		query { 
			allPages {
			 id 
			} 
		}
	");
  },
  getPage: function getPage(id) {
    return fetch("
		query($id: ID!) {
			Page (where: {id: $id}) {
				id, 
				routePath
			}
		}
	", {
      "id": id
    });
  },
  getPageByRoutePath: function getPageByRoutePath(routePath) {
    return fetch("
		query($routePath: String!) {
			allPages (where: {routePath_i: $routePath}) {
				id,
				routePath,
				pageTitle,
				description
			}
		}
	", {
      "routePath": routePath
    });
  }
};
